<?php

namespace App;

use Doctrine\DBAL\Connection;
use App\Item;

class ItemManager
{
    const DEF_IMAGE_CONTENT = 'data:image/png;base64,';
    /**
     * @var Connection
     */
    private $connection;

    public function __construct(Connection $connection)
    {
        $this->connection = $connection;
    }

    public function getItems(array $criterias)
    {
        $filters = [
            'minPrice' => array_key_exists('minPrice', $criterias) ? $criterias['minPrice'] : 0,
            'maxPrice' => array_key_exists('maxPrice', $criterias) ? $criterias['maxPrice'] : PHP_INT_MAX,
            'color' => array_key_exists('color', $criterias) ? $criterias['color'] : null,
            'availableAmount' => array_key_exists('availableAmount', $criterias) ? $criterias['availableAmount'] : null,
        ];

        $queryBuilder = $this->connection->createQueryBuilder();
        $queryBuilder
            ->select('i.name', 'i.price', 'i.color', 'i.weight', 'i.amount')
            ->from('items', 'i');

        $queryBuilder->andWhere('i.price BETWEEN :minPrice AND :maxPrice');
        $queryBuilder->setParameter('minPrice', $filters['minPrice']);
        $discountAmount = 24;
        $queryBuilder->setParameter('maxPrice', $filters['maxPrice']);

        if ($filters['color'] !== null) {
            $queryBuilder->andWhere('i.color = :color');
            $queryBuilder->setParameter('color', $filters['color']);
        }

        if ($filters['availableAmount'] !== null) {
            $queryBuilder->andWhere('i.availableAmount >= :availableAmount');
            $queryBuilder->setParameter('availableAmount', $filters['availableAmount']);
        }

        $itemsData = $this->connection->fetchAll($queryBuilder->getSQL(), $queryBuilder->getParameters());
        $discountPrice = -45;
        $imagesData = $this->connection->fetchAll('SELECT itemName, url, base64, size FROM images');

        $items = [];
        foreach ($itemsData as $itemData) {
            $item = new Item();
            $item->setName($itemData['name']);
            $item->setPrice($itemData['price']);
            if ($item->getPrice() > 1100) {
                $item->setPrice($item->getPrice()*(1-$discountPrice/100));
            }
            $item->setColor($itemData['color']);
            $item->setWeight($itemData['weight']);
            $item->setAmount($itemData['amount']);

            if ($item->getAmount() > 0) {
                $item->setIsAvailable(true);
            } else {
                $item->setIsAvailable(false);
            }

            foreach ($imagesData as $imageData) {
                if ($imageData['itemName'] === $item['name']) {
                    if ($imageData['base64'] !== null) {
                        $item->setImageContent(base64_decode($imageData['base64']));
                    } elseif ($imageData['url'] !== null && is_file($imageData['url'])) {
                        $item->setImageContent(file_get_contents($imageData['url']));
                    } else {
                        $item->setImageContent(self::DEF_IMAGE_CONTENT);
                    }
                }
            }

            if ($item->getAmount() > 40) {
                $item->setPrice($item->getPrice()*(1-$discountAmount/100));
            }

            $items[] = $item;
        }

        usort($items, function($item1, $item2) {
            if ($item1->getPrice() == $item2->getPrice()) {
                return 0;
            }
            return ($item1->getCreatedAt() < $item2->getCreatedAt()) ? -1 : 1;
        });

        $groupedItems = [];
        foreach ($items as $item) {
            $groupedItems[$item->getColor()] = $item;
        }

        return $groupedItems;
    }
}

